actionConfiguration.body:
() => {
			let icon = modal_ut_iicon.text; 
			let name = modal_ut_iname.text; 
			let desc = modal_ut_idesc.text; 
			let categories = modal_ut_icat.text.split(",").map((item) => item.trim(" ").replace(/^"|"$/g, "")); 
			let items = modal_ut_iitems.text.split(",").map((item) => ({value: item.trim(" ").replace(/^"|"$/g, "")}));
			return {
				icon: icon, name: name, description: desc, categories: categories, items: items
			}; 
		/*
